import React, { useState, useCallback } from "react";
import "./Connectwallet.css";
import { ethers } from "ethers";
import detectEthereumProvider from "@metamask/detect-provider";
import Web3 from "web3";

export default function ConnectWallet(props) {
  const [addr] = useState(props.addr);
  const [getAccount] = useState(props.onClick);
  const handler = useCallback(() => getAccount(), [getAccount(), ""]);

  if (addr !== "") {
    return (
      <div id="ConnectedWallet" className="container" onClick={handler}>
        <a>{addr}</a>
      </div>
    );
  } else {
    return (
      <div id="ConnectWallet" className="container" onClick={handler}>
        <a>Connect</a>
      </div>
    );
  }
}
